    and cluster = #{cluster}
    and port = #{port}
    and `status` = #{status}
    and `role` = #{role}
    and rack = #{rack}
    and node_set = #{nodeSet}
    and machine_spec = #{machineSpec}
    and region_id = #{regionId}
    and attributes = #{attributes}
    and delete_flag = 0

select

from es_cluster_role_host_info
where cluster = #{cluster} and rack = #{rack} and status = 1 and delete_flag = 0;

