select

from es_cluster_role_host_info

    and id = #{id}
    and role_cluster_id = #{roleClusterId}
    and `hostname` = #{hostname}
    and ip = #{ip}
    and cluster = #{cluster}
    and port = #{port}
    and `status` = #{status}
    and `role` = #{role}
    and rack = #{rack}
    and node_set = #{nodeSet}
    and machine_spec = #{machineSpec}
    and region_id = #{regionId}
    and attributes = #{attributes}
    AND component_host_id=#{componentHostId}
    and delete_flag = 0

select

from es_cluster_role_host_info
where cluster = #{cluster} and node_set in #{nodeSet}

select

from es_cluster_role_host_info
