mapper.select:
select

from index_template_physical_info
where status > 0
and id = #{id}
and logic_id = #{logicId}
and `name` = #{name}
and cluster = #{cluster}
and role = #{role}
and region_id = #{regionId}
order by id desc

mapper.update:
update index_template_physical_info

    ,`name` = #{name}
    ,expression = #{expression}
    ,rack = #{rack}
    ,shard = #{shard}
    ,shard_routing = #{shardRouting}
    ,version = #{version}
    ,role = #{role}
    ,config = #{config}
    ,region_id = #{regionId}

where id = #{id} and status > 0

update index_template_physical_info
set status = #{status}
where id = #{physicalId}

mapper.select:
select

from index_template_physical_info
where logic_id = #{logicId} and status = #{status}

select

from index_template_physical_info
where status = #{status} and cluster = #{cluster}

select

from index_template_physical_info
where status = #{status} and cluster = #{cluster} and `name` = #{name}

select

from index_template_physical_info
where status = #{status} and cluster = #{cluster}
