mapper.insert:
    insert into operate_record_info (project_name, module_id, operate_id, trigger_way_id,
                                                     user_operation, operate_time,
                                                     content, biz_id

							,operate_project_name

                                                     )
                         values (#{projectName}, #{moduleId}, #{operateId}, #{triggerWayId}, #{userOperation},
                                 #{operateTime}, #{content}, #{bizId}

							,#{operateProjectName}

                                 )

mapper.select:
select

from operate_record_info


		and id = #{id}


		and module_id = #{moduleId}


		and biz_id = #{bizId}


		and operate_id = #{operateId}


		and trigger_way_id = #{triggerWayId}


		and operate_time >= #{beginTime}


		and operate_time <= #{endTime}


		and (project_name = #{projectName} OR project_name LIKE CONCAT(#{projectName}, '%') OR project_name LIKE
		                                                                                       CONCAT('%', #{projectName}) OR
		     project_name LIKE CONCAT('%', #{projectName}, '%'))


		and user_operation = #{userOperation}


		and (content = #{content} OR content LIKE CONCAT(#{content}, '%') OR content LIKE CONCAT('%',
		                                                                                         #{content}) OR
		     content LIKE CONCAT('%', #{content}, '%'))


		and operate_project_name LIKE CONCAT('%', #{operateProjectName}, '%')



	order by ${sortTerm} ${sortType}

LIMIT #{from},#{size}

select count(id)
from operate_record_info


		and id = #{id}


		and module_id = #{moduleId}


		and biz_id = #{bizId}


		and operate_id = #{operateId}


		and trigger_way_id = #{triggerWayId}


		and operate_time >= #{beginTime}


		and operate_time <= #{endTime}


		and (project_name = #{projectName} OR project_name LIKE CONCAT(#{projectName}, '%') OR project_name LIKE
		                                                                                       CONCAT('%', #{projectName}) OR
		     project_name LIKE CONCAT('%', #{projectName}, '%'))


		and user_operation = #{userOperation}


		and (content = #{content} OR content LIKE CONCAT(#{content}, '%') OR content LIKE CONCAT('%',
		                                                                                         #{content}) OR
		     content LIKE CONCAT('%', #{content}, '%'))


		and operate_project_name LIKE CONCAT('%', #{operateProjectName}, '%')


ORDER BY id

select
id
from operate_record_info


		and module_id = #{moduleId}


		and operate_id = #{operateId}


		and trigger_way_id = #{triggerWayId}


		and project_name = #{projectName}


		and user_operation = #{userOperation}


		and operate_project_name = #{operateProjectName}

mapper.update:
delete
from operate_record_info
where module_id = #{moduleId}
	and id < #{id}

delete
from operate_record_info
where cast(operate_time as datetime) < #{saveTime}

mapper.insert:
insert into operate_record_info (project_name, module_id, operate_id, trigger_way_id,
user_operation, operate_time,
content, biz_id, operate_project_name)
select #{projectName}, #{moduleId}, #{operateId}, #{triggerWayId}, #{userOperation},
#{operateTime}, #{content}, #{bizId}, #{operateProjectName}
from dual where not exists (
    select 1 from operate_record_info where project_name=#{projectName} and module_id=#{moduleId} and operate_id=#{operateId}
    and trigger_way_id=#{triggerWayId} and user_operation=#{userOperation} and operate_project_name=#{operateProjectName}
)
