mapper.select:
select

from es_cluster_phy_info

    and id = #{id}
    and cluster = #{cluster}
    and `type` = #{type}
    and data_center = #{dataCenter}
    and idc = #{idc}
    and `level` = #{level}
    and es_version = #{esVersion}
    and health = #{health}
    and active_shard_num = #{activeShardNum}
    and platform_type = #{platformType}
    and resource_type = #{resourceType}

    AND ecm_access = #{ecmAccess}


    AND component_id = #{componentId}


    AND proxy_address = #{proxyAddress}


    AND gateway_ids = #{gatewayIds}

    and is_active = 1;

